package Chapter4;

public class Ex02_PrefixPostFix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		System.out.println(++num);
		System.out.println(num++);
		num++; // 증감 연산자는 단독으로 사용 한다.
		System.out.println(num++);
		System.out.println(++num);
	}

}
